answer:
#include <iostream>
#include <string>
#include <cmath>    // pour exp()
using namespace std;

// ======================================================================
struct Conditions {
  double depth;
  double speed;
  double time;
};

// ======================================================================
struct  Diver {
  const string name;
  const double mass;
  double volume;
  double accel;
  Conditions cond;
};
// ======================================================================
struct Status {
  bool max_depth;
  bool max_speed;
  bool inflated ;
};

// ======================================================================
void display(Diver const& p) {
  cout << p.cond.time << ", " << p.cond.depth << ", " << p.cond.speed
       << ", " << p.accel << endl;
}

// ======================================================================
void evolve(Diver& somebody, Conditions const& initial)
{
  ++somebody.cond.time; // 1 second more

  constexpr double g(9.81);

  const double g_mu(g * (1.0 - 1000.0 * somebody.volume / somebody.mass));
  const double dt(initial.time - somebody.cond.time);
  const double alpha(exp(dt));
  const double beta(initial.speed - g_mu);

  somebody.accel      = - alpha * beta;
  somebody.cond.speed = g_mu - somebody.accel;
  somebody.cond.depth = initial.depth + beta * (alpha - 1.0) + g_mu * dt ;
}

// ======================================================================
void message(Diver const& somebody, string const& msg,
             double value, string const& units)
{ 
  cout << "## (t = " << somebody.cond.time << " s) "
       << somebody.name << " " << msg
       << " (" << value << " " << units << ")" << endl;
}

// ======================================================================
void update_status(Diver& somebody, Conditions& initial, Status& s)
{
  constexpr double target_depth(-40.0);
  constexpr double delta_accel(1e-5);               
  constexpr double return_depth(target_depth - 3.0);
  constexpr double inflated_volume(0.0375);         

  if (not s.max_depth and (somebody.cond.depth <= target_depth)) {
    s.max_depth = true;
    message(somebody, "a atteint la profondeur désirée"s,
            somebody.cond.depth, "m"s);
  }

  if (not s.max_speed and (abs(somebody.accel) < delta_accel)) {
    s.max_speed = true;
    message(somebody, "a atteint sa vitesse maximale"s,
            somebody.accel, "ms-2"s);
  }
}

// ======================================================================

void dive(Diver& somebody) {
  Status status({ false, false, false });
  Conditions initial(somebody.cond);

  while (somebody.cond.depth > -40.0) {
    display(somebody);
    evolve(somebody, initial);
    update_status(somebody, initial, status);
  }
}

// ======================================================================
int main() {
  Diver jacques({ "Jacques"s, 90.0, 0.075, 0.0, {-1.2, 0.8, 0} });
  dive(jacques);
  return 0;
}

