Calculer les limites suivantes.
\begin{multicols}{3}
\begin{enumerate}
\item \(\displaystyle \lim_{x\to 2}\frac{x^3+2x-1}{3x^2-2}\)
\item \(\displaystyle \lim_{x\to 1}\frac{x^3+x^2-2}{x-1}\)
\item \(\displaystyle \lim_{x\to 0}\frac{\cos(2x)-1}{\sin(x^2)}\)
\item \(\displaystyle \lim_{x\to 1}\left(\frac{1}{1-x}-\frac{3}{1-x^3}\right)\)
\item \(\displaystyle \lim_{x\to a}\frac{\cos(x) -\cos(a)}{x-a}\)
\end{enumerate}
\end{multicols}

answer:
\begin{enumerate}
\item Comme le dénominateur ne s'annule pas en \(x=2\), le calcul de cette limite
est direct:
\[
\lim_{x\to 2}\frac{x^3+2x-1}{3x^2-2} = \frac{2^3+2\cdot 2-1}{3\cdot 2^2-2} =
\frac{11}{10}\,.
\]
\item En observant que le dénominateur divise le numérateur, on a 
\begin{align*} 
\lim_{x\to 1}\frac{x^3+x^2-2}{x-1}
&=\lim_{x\to 1}\frac{(x-1)\left(x^2+2x+2\right)}{x-1}\\
&=\lim_{x\to 1}\left(x^2+2x+2\right).
\end{align*}
On peut alors utiliser les propriétés de la limite et calculer:
\begin{align*}
\lim_{x\to 1}\left(x^2+2x+2\right) 
&= \lim_{x\to 1} x^2 + \lim_{x\to 1}2x + 2 \\
&=\Big(\lim_{x\to 1} x\Big)^2 + 2\lim_{x\to 1}x + 2 \\
&= 1^2 + 2\cdot 1 + 2 = 5.
\end{align*}
\item En utilisant que \(\cos(2x)=\cos(x)^2-\sin(x)^2\), on peut récrire le
numérateur comme \(\cos(x)^2-\sin(x)^2-1=-2\sin(x)^2\) et la limite devient
\begin{align*}
\lim_{x\to 0}\frac{-2\sin(x)^2}{\sin(x^2)} 
&= -2\cdot\lim_{x\to 0}\left(\frac{\sin(x)^2}{x^2}\cdot 
\frac{x^2}{\sin(x^2)}\right) \\
&= -2\cdot \lim_{x\to 0}\left(\frac{\sin(x)}{x}\right)^2\cdot 
\frac{1}{ \lim_{x\to 0}\frac{\sin(x^2)}{x^2}}\\
&=-2\cdot 1^2 \cdot 1=-2
\end{align*}
car \(\lim\limits_{x\to 0}\dfrac{\sin(x)}{x}=1\).
\item Comme \(1-x^3=(1-x)(1+x+x^2)\), on peut simplifier la fraction:
\begin{align*}
\frac{1}{1-x}-\frac{3}{1-x^3}
&= \frac{1+x+x^2-3}{1-x^3}\\
&= \frac{x^2+x-2}{(1-x)(1+x+x^2)} \\
&= \frac{(x-1)(x+2)}{(1-x)(1+x+x^2)}\\
&= -\frac{x+2}{x^2+x+1}\,.
\end{align*}
Ainsi,
\[
\lim_{x\to 1}
\Bigl(
\frac{1}{1-x}-\frac{3}{1-x^3}
\Big)
=
-\lim_{x\to 1}
\frac{x+2}{x^2+x+1}
=-1
\]
\item Avec une formule de trigonométrie on peut récrire le numérateur 
\begin{align*}
\lim_{x\to a}&\frac{\cos(x) -\cos(a)}{x-a}\\
&=\lim_{x\to a}\frac{-2\cdot \sin
\left(\frac{x+a}{2}\right)\cdot \sin\left(\frac{x-a}{2}\right)}{x-a}\\
&= -\left(\lim_{x\to a}\sin
\left(\frac{x+a}{2}\right)\right)\cdot\left(\lim_{x\to a}\frac{\sin
\left(\frac{x-a}{2}\right)}{\frac{x-a}{2}}\right) \\
&= -\sin(a)\,,
\end{align*}
car la deuxième limite vaut \(1\).
\end{enumerate}

